mapper.select:
SELECT b.id, b.title, b.contents, b.hit, b.reg_date, b.g_no, b.o_no, b.depth, a.name, a.id AS user_id FROM board b JOIN user a ON a.id = b.user_id
(b.title LIKE CONCAT('%', #{keyword}, '%') OR b.contents LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY b.g_no DESC, b.o_no LIMIT #{limit} OFFSET #{offset}
